// client/src/components/books/BookList.js
import React, { useEffect, useState } from 'react';
import api from '../../utils/api';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    console.log('BookList component mounted');
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      console.log('Fetching books...');
      const response = await api.get('/api/books');
      console.log('Books response:', response.data);
      setBooks(response.data);
    } catch (err) {
      console.error('Error details:', {
        message: err.message,
        response: err.response,
        stack: err.stack
      });
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center p-8">
        <div className="text-xl font-semibold">Loading books...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-red-500 p-4 border rounded">
        Error loading books: {error}
      </div>
    );
  }

  if (!books || books.length === 0) {
    return (
      <div className="text-center p-8">
        <p className="text-gray-600">No books available</p>
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      {books.map((book) => (
        <div key={book._id} className="bg-white p-6 rounded-lg shadow-md">
          <h3 className="text-xl font-semibold mb-2">{book.title}</h3>
          <p className="text-gray-600">Author: {book.author}</p>
          <p className="text-gray-600">Genre: {book.genre}</p>
          <p className="text-gray-600">ISBN: {book.isbn}</p>
          <p className="text-gray-600">
            Available: {book.availableCopies} / {book.quantity}
          </p>
        </div>
      ))}
    </div>
  );
};

export default BookList;
