// client/src/components/user/BorrowHistory.js
import React, { useState, useEffect } from 'react';
import api from '../../utils/api';

const BorrowHistory = () => {
  const [borrowings, setBorrowings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBorrowings = async () => {
      try {
        const response = await api.get('/api/borrows/history');
        setBorrowings(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching borrow history:', error);
        setLoading(false);
      }
    };

    fetchBorrowings();
  }, []);

  const handleReturn = async (borrowId) => {
    try {
      console.log('BorrowId:', borrowId); // Add this log
      console.log('Full URL:', `/api/borrows/${borrowId}/return`); // Add this log
      const response = await api.put(`/api/borrows/${borrowId}/return`);

      // Refresh the borrowings list
      const historyResponse = await api.get('/api/borrows/active');
      setBorrowings(historyResponse.data);
      alert('Book returned successfully!');
    } catch (error) {
      // Log the complete error object
      console.error('Complete error object:', error);
      console.error('Error response data:', error.response?.data);
      console.error('Error status:', error.response?.status);

      alert(
        error.response?.data?.message || 
        error.message || 
        'Error returning book. Please try again.'
      );
    }
  };
  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Borrowing History</h2>
      <div className="space-y-4">
        {borrowings.map((borrow) => (
          <div
            key={borrow._id}
            className="border rounded-lg p-4 flex justify-between items-center"
          >
            <div>
              <h3 className="text-xl font-semibold">{borrow.book.title}</h3>
              <p className="text-gray-600">
                Issue Date: {new Date(borrow.issueDate).toLocaleDateString()}
              </p>
              <p className="text-gray-600">
                Due Date: {new Date(borrow.dueDate).toLocaleDateString()}
              </p>
              <p
                className={`${
                  borrow.status === 'overdue' ? 'text-red-600' : 'text-gray-600'
                }`}
              >
                Status: {borrow.status}
              </p>
            </div>
            {borrow.status === 'borrowed' && (
              <button
                onClick={() => handleReturn(borrow._id)}
                className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
              >
                Return Book
              </button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default BorrowHistory;
