// client/src/components/admin/AdminDashboard.js
import React, { useState, useEffect } from 'react';
// import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
// import AuthContext from '../../context/AuthContext';
import axios from 'axios';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalBooks: 0,
    availableBooks: 0,
    totalUsers: 0,
    activeLoans: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDashboardStats = async () => {
      try {
        setLoading(true);
        setError(null);

        // Get books stats
        const booksResponse = await axios.get('/api/books');
        const books = booksResponse.data;

        // Calculate book statistics safely
        const totalBooks = books.reduce((sum, book) => {
          const quantity = parseInt(book.quantity) || 0;
          return sum + quantity;
        }, 0);

        const availableBooks = books.reduce((sum, book) => {
          const available = parseInt(book.availableCopies) || 0;
          return sum + available;
        }, 0);

        // Get users count
        const usersResponse = await axios.get('/api/users');
        const totalUsers = Array.isArray(usersResponse.data) ? 
          usersResponse.data.length : 0;

        // Get active loans
        const loansResponse = await axios.get('/api/loans/active');
        const activeLoans = Array.isArray(loansResponse.data) ?
          loansResponse.data.length : 0;

        setStats({
          totalBooks,
          availableBooks,
          totalUsers,
          activeLoans
        });

      } catch (error) {
        console.error('Error fetching dashboard stats:', error);
        setError('Failed to load dashboard statistics. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardStats();
  }, []);

  if (loading) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold">Admin Dashboard</h1>
        {/* <p className="text-gray-600">Welcome, {user.name}</p> */}
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <div className="bg-white p-6 rounded-lg shadow">
          <div className="text-sm font-medium text-gray-500 mb-2">Total Books</div>
          <div className="text-2xl font-bold">{stats.totalBooks}</div>
          <p className="text-sm text-gray-500">{stats.availableBooks} available</p>
        </div>

        <div className="bg-white p-6 rounded-lg shadow">
          <div className="text-sm font-medium text-gray-500 mb-2">Total Users</div>
          <div className="text-2xl font-bold">{stats.totalUsers}</div>
          <p className="text-sm text-gray-500">Registered users</p>
        </div>

        <div className="bg-white p-6 rounded-lg shadow">
          <div className="text-sm font-medium text-gray-500 mb-2">Active Loans</div>
          <div className="text-2xl font-bold">{stats.activeLoans}</div>
          <p className="text-sm text-gray-500">Books currently borrowed</p>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="space-y-4">
        <h2 className="text-xl font-semibold mb-4">Quick Actions</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          <Link
            to="/admin/add-book"
            className="flex items-center p-4 bg-blue-50 rounded-lg hover:bg-blue-100 transition-colors"
          >
            <div>
              <h3 className="font-semibold">Manage Books</h3>
              <p className="text-sm text-gray-600">Add books</p>
            </div>
          </Link>

          <Link
            to="/admin/manage-users"
            className="flex items-center p-4 bg-green-50 rounded-lg hover:bg-green-100 transition-colors"
          >
            <div>
              <h3 className="font-semibold">Manage Users</h3>
              <p className="text-sm text-gray-600">View and manage user accounts</p>
            </div>
          </Link>

          <Link
            to="/admin/issued-books"
            className="flex items-center p-4 bg-purple-50 rounded-lg hover:bg-purple-100 transition-colors"
          >
            <div>
              <h3 className="font-semibold">View Issued Books</h3>
              <p className="text-sm text-gray-600">Track borrowed books</p>
            </div>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
