// client/src/components/admin/ManageBook.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom'; // For handling route parameters
import api from '../../utils/api';

const ManageBook = () => {
  const { bookId } = useParams(); // Get book ID from URL (if exists)
  const [bookData, setBookData] = useState({
    title: '',
    author: '',
    isbn: '',
    genre: '',
    quantity: 1,
  });
  const [isEditMode, setIsEditMode] = useState(false); // Flag to determine edit mode
  const navigate = useNavigate(); // For redirecting after actions

  // If bookId exists, fetch the book details for updating
  useEffect(() => {
    if (bookId) {
      setIsEditMode(true);
      const fetchBookDetails = async () => {
        try {
          const response = await api.get(`/api/books/${bookId}`);
          setBookData(response.data);
        } catch (error) {
          console.error('Error fetching book details:', error);
          alert('Error fetching book details');
        }
      };
      fetchBookDetails();
    } else {
      setIsEditMode(false); // It's in add mode if no bookId
    }
  }, [bookId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isEditMode) {
        // Update book
        await api.put(`/api/books/${bookId}`, bookData);
        alert('Book updated successfully!');
      } else {
        // Add new book
        await api.post('/api/books', bookData);
        alert('Book added successfully!');
      }
      // Clear form and redirect to the book list
      setBookData({ title: '', author: '', isbn: '', genre: '', quantity: 1 });
      navigate('/admin/books'); // Redirect after submit
    } catch (error) {
      console.error('Error saving book:', error);
      alert('Error saving book');
    }
  };

  const handleDelete = async () => {
    try {
      if (window.confirm('Are you sure you want to delete this book?')) {
        await api.delete(`/api/books/${bookId}`);
        alert('Book deleted successfully!');
        navigate('/admin/books'); // Redirect to book list after deletion
      }
    } catch (error) {
      console.error('Error deleting book:', error);
      alert('Error deleting book');
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">{isEditMode ? 'Update Book' : 'Add New Book'}</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block mb-1">Title</label>
          <input
            type="text"
            className="w-full p-2 border rounded"
            value={bookData.title}
            onChange={(e) => setBookData({ ...bookData, title: e.target.value })}
            required
          />
        </div>
        <div>
          <label className="block mb-1">Author</label>
          <input
            type="text"
            className="w-full p-2 border rounded"
            value={bookData.author}
            onChange={(e) => setBookData({ ...bookData, author: e.target.value })}
            required
          />
        </div>
        <div>
          <label className="block mb-1">ISBN</label>
          <input
            type="text"
            className="w-full p-2 border rounded"
            value={bookData.isbn}
            onChange={(e) => setBookData({ ...bookData, isbn: e.target.value })}
            required
          />
        </div>
        <div>
          <label className="block mb-1">Genre</label>
          <input
            type="text"
            className="w-full p-2 border rounded"
            value={bookData.genre}
            onChange={(e) => setBookData({ ...bookData, genre: e.target.value })}
            required
          />
        </div>
        <div>
          <label className="block mb-1">Quantity</label>
          <input
            type="number"
            className="w-full p-2 border rounded"
            value={bookData.quantity}
            onChange={(e) => setBookData({ ...bookData, quantity: Number(e.target.value) })}
            min="1"
            required
          />
        </div>
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          {isEditMode ? 'Update Book' : 'Add Book'}
        </button>
      </form>

      {isEditMode && (
        <button
          type="button"
          onClick={handleDelete}
          className="bg-red-500 text-white px-4 py-2 mt-4 rounded hover:bg-red-600"
        >
          Delete Book
        </button>
      )}
    </div>
  );
};

export default ManageBook;
