import React, { useContext, useState } from 'react';
import AuthContext from '../../context/AuthContext';
import BookList from '../books/BookList';
import SearchBooks from '../books/SearchBooks';

const UserDashboard = () => {
  const { user } = useContext(AuthContext);
  const [books, setBooks] = useState([]); // State to hold the searched books

  const handleSearchResults = (results) => {
    setBooks(results); // Update the state with search results
  };

  return (
    <div className="container mx-auto px-4">
      <h1 className="text-2xl font-bold mb-6">Welcome, {user.name}</h1>
      <SearchBooks onSearch={handleSearchResults} /> {/* Pass the handler */}
      <BookList books={books} /> {/* Pass the books to BookList */}
    </div>
  );
};

export default UserDashboard;
